mapper.select:
select count(*) from final_measuret_check


    f_measure_check_id = #{params.fid}


    order_id = #{params.oid}

select  f_measure_check_id, order_id, check_item, cdate, measure_data, fm.emp_id, result, note,emp.emp_name
FROM final_measuret_check fm left join employee emp   on fm.emp_id=emp.emp_id


    f_measure_check_id = #{params.fid}


    order_id = #{params.oid}


limit #{params.limit} offset #{params.offset}

mapper.update:
update final_measuret_check
set note = #{note}
where f_measure_check_id=#{id}
